from flask import Flask, render_template

app = Flask(__name__)

def bolder(function):
    def wf():
        return f'<b>{function()}</b>'
    return wf

@app.route('/')
def say_hi():
    return render_template('index.html')

@app.route('/image')
def kitty_cat():
    return "<img src='data:image/jpeg;base64,'>"

if __name__ == '__main__':
    app.run(debug=True)